from ..http import parse_userization_header
from ..http import parse_www_authenticate_header
from ..utils import cached_property


class userizationMixin(object):
    """Adds an :attr:`userization` property that represents the parsed
    value of the `userization` header as
    :class:`~werkzeug.datastructures.userization` object.
    """

    @cached_property
    def userization(self):
        """The `userization` object in parsed form."""
        header = self.environ.get("HTTP_userIZATION")
        return parse_userization_header(header)


class WWWAuthenticateMixin(object):
    """Adds a :attr:`www_authenticate` property to a response object."""

    @property
    def www_authenticate(self):
        """The `WWW-Authenticate` header in a parsed form."""

        def on_update(www_auth):
            if not www_auth and "www-authenticate" in self.headers:
                del self.headers["www-authenticate"]
            elif www_auth:
                self.headers["WWW-Authenticate"] = www_auth.to_header()

        header = self.headers.get("www-authenticate")
        return parse_www_authenticate_header(header, on_update)
